import React, { useState, useEffect } from 'react';
import { identityService, revocationService, monitoringService } from '../services/api';

function GovernmentPage() {
  const [identities, setIdentities] = useState([]);
  const [revocations, setRevocations] = useState([]);
  const [anomalies, setAnomalies] = useState([]);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [searchDid, setSearchDid] = useState('');
  const [searchVcId, setSearchVcId] = useState('');

  useEffect(() => {
    loadMonitoringData();
  }, []);

  const loadMonitoringData = async () => {
    setLoading(true);
    try {
      const anomaliesResponse = await monitoringService.getTrafficAnomalies();
      setAnomalies(anomaliesResponse.data);
    } catch (error) {
      console.error('Erro ao carregar dados:', error);
    }
    setLoading(false);
  };

  const handleSearchIdentity = async () => {
    if (!searchDid) return;
    setLoading(true);
    try {
      const response = await identityService.getIdentity(searchDid);
      setIdentities([response.data]);
      setMessage('');
    } catch (error) {
      setMessage('Identidade nÃ£o encontrada');
      setIdentities([]);
    }
    setLoading(false);
  };

  const handleSearchRevocation = async () => {
    if (!searchVcId) return;
    setLoading(true);
    try {
      const response = await revocationService.getRevocationStatus(searchVcId);
      setRevocations([response.data]);
      setMessage('');
    } catch (error) {
      setMessage('RevogaÃ§Ã£o nÃ£o encontrada');
      setRevocations([]);
    }
    setLoading(false);
  };

  const getComplianceStatus = (identity) => {
    if (identity.gov_link_status === 'LINKED') {
      return { status: 'Conforme', color: '#00FF00' };
    }
    return { status: 'Pendente', color: '#FFFF00' };
  };

  return (
    <div className="government-page">
      <h2 style={{ color: '#00FF00' }}>ğŸ›ï¸ Painel do Governo - GovernanÃ§a Digital</h2>
      <div className="intro-section" style={{ 
        padding: '20px', 
        border: '1px solid #00FF00', 
        borderRadius: '8px', 
        marginBottom: '30px',
        backgroundColor: '#1a1a1a'
      }}>
        <h3 style={{ color: '#00FF00', marginBottom: '15px' }}>ğŸ† LideranÃ§a em Identidade Digital Nacional</h3>
        <p style={{ marginBottom: '15px', lineHeight: '1.6' }}>
          O sistema "NÃ£o Sou RobÃ´" estabelece um novo padrÃ£o para identidade digital no Brasil, 
          integrando-se perfeitamente com Gov.br e CIN para criar um ecossistema seguro e confiÃ¡vel 
          de identificaÃ§Ã£o digital descentralizada.
        </p>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '15px' }}>
          <div style={{ padding: '15px', border: '1px solid #333', borderRadius: '4px' }}>
            <strong style={{ color: '#00FF00' }}>ğŸ“œ Conformidade LGPD:</strong><br/>
            Garante proteÃ§Ã£o total de dados pessoais com Zero-Knowledge Proofs
          </div>
          <div style={{ padding: '15px', border: '1px solid #333', borderRadius: '4px' }}>
            <strong style={{ color: '#00FF00' }}>ğŸ”— Interoperabilidade:</strong><br/>
            PadrÃµes W3C DID compatÃ­veis com sistemas internacionais
          </div>
          <div style={{ padding: '15px', border: '1px solid #333', borderRadius: '4px' }}>
            <strong style={{ color: '#00FF00' }}>ğŸšª Soberania Digital:</strong><br/>
            Infraestrutura nacional independente de big techs estrangeiras
          </div>
        </div>
      </div>

      <div className="card">
        <h3>ğŸ“œ DefiniÃ§Ã£o de PadrÃµes - Arquitetura Nacional</h3>
        <div style={{ 
          padding: '15px', 
          border: '1px solid #333', 
          borderRadius: '6px', 
          marginBottom: '15px',
          backgroundColor: '#2a2a2a'
        }}>
          <p style={{ marginBottom: '10px' }}>
            <strong>EstabeleÃ§a os padrÃµes nacionais para identidade digital:</strong>
          </p>
          <ul style={{ marginLeft: '20px', lineHeight: '1.6' }}>
            <li><strong>Formato DID:</strong> Estrutura padronizada para identidades brasileiras</li>
            <li><strong>NÃ­veis de ValidaÃ§Ã£o:</strong> CritÃ©rios de seguranÃ§a por setor</li>
            <li><strong>IntegraÃ§Ã£o Gov.br:</strong> Protocolos de comunicaÃ§Ã£o seguros</li>
            <li><strong>Conformidade Legal:</strong> AderÃªncia Ã  LGPD e Marco Civil</li>
          </ul>
        </div>
        <p style={{ color: '#ccc', marginBottom: '15px' }}>
          Configure os parÃ¢metros fundamentais do sistema nacional:
        </p>

        <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginTop: '20px' }}>
          <div>
            <label style={{ display: 'block', marginBottom: '10px' }}>Formato de Identidade Digital:</label>
            <select className="input">
              <option>DID W3C PadrÃ£o</option>
              <option>DID Gov.br Customizado</option>
              <option>DID CIN Integrado</option>
            </select>
          </div>

          <div>
            <label style={{ display: 'block', marginBottom: '10px' }}>NÃ­vel de ValidaÃ§Ã£o:</label>
            <select className="input">
              <option>Alto (Biometria + Documentos)</option>
              <option>MÃ©dio (Documentos)</option>
              <option>BÃ¡sico (DeclaratÃ³rio)</option>
            </select>
          </div>
        </div>

        <div style={{ marginTop: '20px' }}>
          <label style={{ display: 'block', marginBottom: '10px' }}>PerÃ­odo de Validade (dias):</label>
          <input type="number" className="input" defaultValue="365" />
        </div>

        <button className="btn" style={{ marginTop: '20px' }}>
          Certificar Formatos de Identidade Digital
        </button>
      </div>

      <div className="card">
        <h3>ğŸ” Monitoramento de Identidades - Auditoria e Conformidade</h3>
        <div style={{ 
          padding: '15px', 
          border: '1px solid #333', 
          borderRadius: '6px', 
          marginBottom: '15px',
          backgroundColor: '#2a2a2a'
        }}>
          <p style={{ marginBottom: '10px' }}>
            <strong>Capacidades de auditoria e monitoramento:</strong>
          </p>
          <ul style={{ marginLeft: '20px', lineHeight: '1.6' }}>
            <li><strong>Auditoria LGPD:</strong> VerificaÃ§Ã£o de conformidade com proteÃ§Ã£o de dados</li>
            <li><strong>Rastreabilidade:</strong> HistÃ³rico completo de operaÃ§Ãµes sem expor dados</li>
            <li><strong>MÃ©tricas Nacionais:</strong> EstatÃ­sticas de adoÃ§Ã£o e uso do sistema</li>
            <li><strong>DetecÃ§Ã£o de Anomalias:</strong> IdentificaÃ§Ã£o de padrÃµes suspeitos</li>
